import { Schema, model, models } from 'mongoose'

const UserSchema = new Schema({
  email: {
    type: String,
    unique: [true, 'Email already exists'],
    required: [true, 'Email is required']
  },
  username: {
    type: String,
    required: [true, 'Username is required'],
    match: [
      /^(?=.{8,20}$)(?![_.])(?!.*[_.]{2})[a-zA-Z0-9._]+(?<![_.])$/,
      'Username invalid, it should contain 8-20 alphanumeric letters and be unique!'
    ]
  },
  image: {
    type: String
  }
})

const User = models.User || model('User', UserSchema)

export default User

import Prompt from '@/models/prompt'
import User from '@/models/user'
import { connectToDB } from '@/utils/database'

export const GET = async request => {
  const { searchParams } = new URL(request.url)
  const searchText = searchParams.get('searchText')

  try {
    await connectToDB()
    // æ‹†åˆ†å…³é”®è¯
    const keywordArray = searchText.split(' ').filter(kw => kw.trim() !== '')

    // æ„å»ºå…³é”®è¯è¡¨è¾¾å¼ç»„
    const regexArray = keywordArray.map(kw => new RegExp(kw, 'i'))

    // æŸ¥è¯¢æ‰€æœ‰æ»¡è¶³æ¡ä»¶çš„ç”¨æˆ·
    const userConditions = regexArray.map(kw => ({ username: { $regex: kw } }))
    const users = await User.find({ $or: userConditions })
    const userIds = users.map(user => user._id)

    // æŸ¥è¯¢æ»¡è¶³æ¡ä»¶çš„prompts
    const prompts = await Prompt.find({
    // ç”¨ç©ºæ ¼åŒºåˆ†çš„å…³é”®è¯æ›´åˆç†çš„æ–¹å¼æ˜¯ç”¨ andï¼Œä½†æˆ‘è¿™é‡Œå…ˆç”¨ or äº†ï¼Œçœ‹èµ·æ¥æ˜¾å¾—å¤šä¸€ç‚¹
      $or: [
        { prompt: { $in: regexArray } },
        { tag: { $in: regexArray } },
        { creator: { $in: userIds } }
      ]
    }).populate('creator') // populate æ–¹æ³•ï¼Œè‡ªåŠ¨å…³è” User è¡¨ï¼Œä½†æ˜¯å‰æéœ€è¦åœ¨è¡¨è®¾è®¡æ—¶ä½¿ç”¨
    // ref æŒ‡å‘ä½ è¦å…³è”çš„è¡¨

    return new Response(JSON.stringify(prompts), { status: 200 })
  } catch (error) {
    return new Response('Failed to fetch prompts', { status: 500 })
  }
}

import { useState, useEffect } from 'react'

const useSearchPosts = (initialSearchText = '') => {
  const [searchText, setSearchText] = useState(initialSearchText)
  const [posts, setPosts] = useState([])
  const [timeoutId, setTimeoutId] = useState(null)

  // è·å–æ‰€æœ‰ prompt
  const fetchPosts = async () => {
    const response = await fetch('/api/prompt')
    const data = await response.json()
    setPosts(data)
  }

  // æ ¹æ®æœç´¢å…³é”®è¯è·å– prompt
  const fetchSearchPosts = async () => {
    const response = await fetch('/api/prompt/search?searchText=' + searchText)
    const data = await response.json()
    setPosts(data)
  }

  useEffect(() => {
    fetchPosts()
  }, [])

  // é˜²æŠ–
  useEffect(() => {
    if (searchText.trim()) {
      const newTimeout = setTimeout(() => {
        fetchSearchPosts()
      }, 1000)

      setTimeoutId(newTimeout)
      return () => clearTimeout(newTimeout)
    } else {
      fetchPosts()
      return () => clearTimeout(timeoutId)
    }
  }, [searchText])

  const handleSearchChange = e => {
    setSearchText(e.target.value)
  }

  const handleSubmit = e => {
    e.preventDefault()
    clearTimeout(timeoutId)
    fetchSearchPosts()
  }

  return {
    searchText,
    posts,
    fetchPosts,
    handleSearchChange,
    handleSubmit
  }
}

export default useSearchPosts

'use client'

import React, { useContext, useState, useEffect } from 'react'
import PromptCard from './PromptCard'
import basePathContext from '@/context/basePathContext'
import useSearchPosts from '@/hooks/useSearchPosts'

const PromptCardList = ({ data, handleTagClick }) => {
  return (
    <div className="mt-16 prompt_layout">
      {data.map(post => (
        <PromptCard key={post.id} post={post} handleTagClick={handleTagClick} />
      ))}
    </div>
  )
}

const Feed = ({ basePath }) => {
  const { searchText, posts, fetchPosts, handleSearchChange, handleSubmit } =
    useSearchPosts()

  useEffect(() => {
    fetchPosts()
  }, [])

  return (
    <basePathContext.Provider value={basePath}>
      <section className="feed">
        <form className="relative w-full flex-center" onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Search for a tag or username"
            value={searchText}
            onChange={handleSearchChange}
            required
            className="search_input peer"
          />
        </form>

        <PromptCardList data={posts} handleTagClick={() => {}} />
      </section>
    </basePathContext.Provider>
  )
}

export default Feed
